mapper.select:
select t.*, sd.name serviceDepartName, u.name umineName, ed.name equipDepartName, ty.value typeValue, f.name orgName from monitor_check t left join unit_service_depart sd on t.service_id = sd.id left join unit_umine u on t.umine_id = u.id left join unit_equip_depart ed on t.equip_depart_id = ed.id left join config_monitor_check_type ty on t.type_id = ty.id left join supervision_org f on t.org_id = f.id where 1=1
and instr(sd.name,#{name})>0 or instr(u.name,#{name})>0 or instr(ed.name,#{name})>0
and instr(t.content,#{content})>0
and t.type_id in (
#{item}
)
and DATE(t.start_date) <= DATE(#{start_date})
and DATE(t.end_date) >= DATE(#{start_date})
and t.service_id = #{servicedepartId}
and t.equip_depart_id = #{equipdepartId}
and t.umine_id = #{umineId}
order by t.modify_date desc

select count(1) from monitor_check t where  t.content=#{content} and t.type_id=#{typeId} 
and t.start_date={startDate} and t.end_date=#{endDate}

  and t.equip_depart_id = #{equipdepartId}


  and t.umine_id = #{umineId}


  and t.service_id = #{serviceId}
