mapper.select:
select t.*, u.name umineName, up.name uminePlaceName, s.value uminePlaceStatusTypeValue, ct.value checkTypeValue, sqt.value questionTypeValue, sqn.value questionNatureValue, rs.value reformStatusTypeValue from security_umine_place t left join unit_umine u on t.umine_id = u.id left join unit_umine_place up on t.umine_place_id = up.id left join config_umine_place_status s on t.status_id = s.id left join config_security_check_type ct on t.check_type_id = ct.id left join config_umine_place_security_question_type sqt on t.question_type_id = sqt.id left join config_umine_place_security_question_nature sqn on t.question_nature_id = sqn.id left join config_security_reform_status rs on t.reform_status_id = rs.id where 1=1
and instr(u.name,#{umineName})>0
and instr(up.name,#{uminePlaceName})>0
and t.status_id in (
#{item}
)
and t.check_type_id in (
#{item}
)
and instr(t.content,#{content})>0
and substr(t.find_date,0,10) >= substr(#{start_date},0,10)
and substr(t.find_date,0,10) <= substr(#{end_date},0,10)
and t.question_type_id in (
#{item}
)
and t.question_nature_id in (
#{item}
)
and t.reform_status_id in (
#{item}
)
and t.umine_id = #{umineId}
and t.umine_place_id = #{umineplaceId}
order by t.modify_date desc
order by u.name,up.name

select count(1) from security_umine_place t WHERE t.umine_id=#{umineId}

  and t.umine_place_id = #{uminePlaceId}


  and t.status_id = #{statusId}

and t.content=#{content} and t.find_date=#{findDate};
